-- Input Handling
local dragging = nil
local dragStart = nil
local startPos = nil
local lastMouse1 = false
local lastF1 = false
local lastRescanKey = false

spawn(function()
    while true do
        wait(0.01)
        if isrbxactive() then
            local mouse1 = ismouse1pressed()
            local mPos = Vector2.new(Mouse.X, Mouse.Y)

            local function clickedOn(element, pos)
                return element.Visible
                    and pos.X >= element.Position.X and pos.X <= element.Position.X + element.Size.X
                    and pos.Y >= element.Position.Y and pos.Y <= element.Position.Y + element.Size.Y
            end

            if mouse1 and not lastMouse1 then
                if clickedOn(HOMEtabB, mPos) then
                    currentTab = "Home"
                    TabHomeContainerSetVisible(true)
                    TabTabVisualsContainerSetVisible(false)
                    TabMiscTABcontainer1SetVisible(false)

                elseif clickedOn(TabVisualsB, mPos) then
                    currentTab = "Visuals"
                    TabHomeContainerSetVisible(false)
                    TabTabVisualsContainerSetVisible(true)
                    TabMiscTABcontainer1SetVisible(false)

                elseif clickedOn(MiscTAB, mPos) then
                    currentTab = "Misc"
                    TabHomeContainerSetVisible(false)
                    TabTabVisualsContainerSetVisible(false)
                    TabMiscTABcontainer1SetVisible(true)

                elseif clickedOn(MainUIsystem, mPos) and mPos.Y <= MainUIsystem.Position.Y + 43 then
                    dragging = MainUIsystem
                    dragStart = mPos
                    startPos = MainUIsystem.Position

                elseif clickedOn(Angler, mPos) or clickedOn(AnglerBg, mPos) then
                    AnglerIsChecked = not AnglerIsChecked
